import React, { useState } from 'react';
import '../../App.css';
import Button from '../Button/Button';
import PopupWithMessage from '../PopupWithMessage/PopupWithMessage';

function App() {
  const [isDisable, setIsBisable] = useState(false);
  const [isOpenPopup, setIsOpenPopup] = useState(false);

  function openPopup() {
    setIsOpenPopup(true);
  }

  function closePopup() {
    setIsOpenPopup(false);
  }

  function clickButton() {
    setIsBisable(true);
    setIsOpenPopup(true);
  }
  return (
    <>
    <Button clickButton={clickButton} text='тестик попапа'/>
    {isOpenPopup && <PopupWithMessage text='текст' onClose={closePopup}></PopupWithMessage>} 
    </>
  );
}

export default App;
