      name: 'Sirius',
      lastName: 'Snake',
      healthPoints: 100,
      manaPoints: 1000,
      status: 'active',
    },

    {
      name: 'Hermiona',
      lastName: 'Granger',
      healthPoints: 100,
      manaPoints: 1000,
      status: 'active',
    },
  ];

  function findFighter() {
    setIsBlockButtonFind(true);
  }

